mapper.select:
SELECT 

FROM msg_sms a

WHERE a.id = #{id}

SELECT 

FROM msg_sms a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND mid = #{mid}


		AND tele = #{tele}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM msg_sms a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE msg_sms SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
